import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { AuthService } from 'src/app/servicios/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  usuario = '';
  password = '';
  loginIncorrecto = false;

  constructor(
    private authService: AuthService,
    private router: Router,
    private route: ActivatedRoute // Inyectamos ActivatedRoute para leer parámetros de la URL

  ) { }

  hacerLogin(): void {
    this.authService.login(this.usuario, this.password);
    // Usamos el valor reactivo `isLoggedIn` para redirigir después del login

    if (this.authService.isLoggedIn()) {
      // Obtener la URL de retorno, si está presente
      const returnUrl = this.route.snapshot.queryParams['returnUrl'] || '/inicio';
      this.router.navigate([returnUrl]); // Redirigir al usuario a la URL que intentaba acceder
    } else {
      this.loginIncorrecto = true;
    }
  }
}
<div class="container-fluid py-3 px-4">
  <h5>Login</h5>

  <div class="row">
    <div class="col-md-6 mb-2">
      <input
        type="text"
        [(ngModel)]="usuario"
        class="form-control"
        placeholder="Usuario">
    </div>

    <div class="col-md-6 mb-2">
      <input
        type="password"
        [(ngModel)]="password"
        class="form-control"
        placeholder="Contraseña">
    </div>

    <div class="col-12">
      <button
        type="button"
        class="btn btn-primary"
        (click)="hacerLogin()">
        Entrar
      </button>
    </div>
  </div>
</div>
