import { Injectable, signal, inject, computed } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private url: string = 'http://localhost:4216';
  private http = inject(HttpClient);
  private router = inject(Router);

  // Signals para el estado del usuario [cite: 85]
  public usuario = signal<any>(null);
  public isLoggedIn = signal<boolean>(false);

  // Solución al error isAdmin: Creamos un signal computado 
  public isAdmin = computed(() => this.usuario()?.rol === 'administrador');

  login(datos: any) {
    return this.http.get<any[]>(`${this.url}/accesoDB`).subscribe({
    next: (usuarios: any[]) => {
      const user = usuarios.find(u => u.username === datos.username && u.password === datos.password);
      if (user) {
        this.usuario.set(user);
        this.isLoggedIn.set(true);
        this.router.navigate(['/inicio']);
      } else {
        alert("Usuario no encontrado en el servidor de Node");
      }
    },
    error: (err) => {
      console.error("Error de conexión con Node:", err);
      alert("Error 404: El servidor de Node no reconoce la ruta /accesoDB");
    }
  });
  }

  // Solución al error de registro: Añadimos la función 
  registro(nombre: string, username: string, password: string, imagen: string) {
    const nuevoUsuario = {
      nombre,
      username,
      password,
      rol: 'usuario', // Por defecto se registran como usuarios [cite: 11]
      imagen // Aquí se guarda el Base64 [cite: 52]
    };

    return this.http.post(`${this.url}/accesoDB`, nuevoUsuario).subscribe({
      next: () => {
        alert("Registro completado con éxito");
        this.router.navigate(['/login']);
      },
      error: () => alert("Error al registrar el usuario")
    });
  }

  // Solución al error logout [cite: 19]
  logout() {
    this.usuario.set(null);
    this.isLoggedIn.set(false);
    this.router.navigate(['/login']);
  }
}
