import { Injectable, inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class AccesoApiService {
  private http = inject(HttpClient);
  // Usamos el puerto 3000 que es donde corre tu servidor de Node/JSON
  private apiUrl = 'http://localhost:3000/accesoDB';

  // üîê LOGIN: Env√≠a los datos a la ruta /leer de tu servidor Node
  login(username: string, password: string): Observable<any> {
    return this.http.post<any>(
      `${this.apiUrl}/leer`,
      { username, password }
    );
  }

  // üìù REGISTRO: Incluye la imagen en Base64 y el rol por defecto
  registro(nombre: string, username: string, password: string, imagen: string): Observable<any> {
    return this.http.post<any>(
      `${this.apiUrl}/grabar`,
      { 
        nombre, 
        username, 
        password, 
        imagen, 
        rol: 'usuario' // Aqu√≠ es donde se define para la base de datos
      }
    );
  }
}
