import { Injectable, signal } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Injectable({
  providedIn: 'root'
})
export class UsuariosService {
  private baseUrl: string = 'http://localhost:3000/accesoDB';

  // Signals para manejar el estado
  public listadoUsuarios = signal<any[]>([]);

  constructor(private http: HttpClient) {}

  leerUsuarios() {
    this.http.get<any[]>(`${this.baseUrl}/leer`).subscribe(users => {
      this.listadoUsuarios.set(users);
    });
  }

  eliminarUsuario(id: number) {
    this.http.delete(`${this.baseUrl}/borrar/${id}`).subscribe(() => {
      this.leerUsuarios(); 
    });
  }

  actualizarUsuario(usuario: any) {
    this.http.post(`${this.baseUrl}/grabar`, usuario).subscribe(() => {
      this.leerUsuarios();
    });
  }
}
