import { Injectable, signal, computed, inject } from '@angular/core';
import { Router } from '@angular/router';
import { AccesoApiService } from './acceso-api.service';

@Injectable({ providedIn: 'root' })
export class AuthService {
  private accesoApi = inject(AccesoApiService);
  private router = inject(Router);

  private _isLoggedIn = signal(false);
  isLoggedIn = this._isLoggedIn.asReadonly();

  public _usuario = signal<any | null>(null);
  usuario = this._usuario.asReadonly();

  // Requisito 1: Signal computado para saber si es Admin
  // Signal que detecta si el usuario es admin
  public isAdmin = computed(() => this._usuario()?.rol === 'administrador');
  login(username: string, password: string): void {
    this.accesoApi.login(username, password).subscribe({
      next: (resp) => {
        if (resp.ok) {
          this._isLoggedIn.set(true);
          this._usuario.set(resp.usuario);
          localStorage.setItem('username', resp.usuario.username);
          localStorage.setItem('rol', resp.usuario.rol); // Guardamos rol
          this.router.navigate(['/inicio']);
        }
      }
    });
  }

  // Quitamos 'rol' de los argumentos de la función principal
  registro(nombre: string, username: string, password: string, imagen: string, rol: string): void {
  // Pasamos el rol recibido al API Service
  this.accesoApi.registro(nombre, username, password, imagen, rol).subscribe({
    next: (resp) => {
      if (resp.ok) {
        alert("Usuario creado como: " + rol);
        this.router.navigate(['/login']);
      }
    }
  });
}

  logout(): void {
    this._isLoggedIn.set(false);
    this._usuario.set(null);
    localStorage.clear();
    this.router.navigate(['/login']);
  }
}
