import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/servicios/auth.service';

@Component({
  selector: 'app-registro',
  templateUrl: './registro.component.html',
  styleUrls: ['./registro.component.css']
})
export class RegistroComponent {
  nombre: string = '';
  username: string = '';
  password: string = '';
  imagenBase64: string = ''; // Nueva propiedad para la imagen

  constructor(private authService: AuthService,     
              private router: Router) 
  { }

  // Funci√≥n para capturar el archivo y convertirlo
  onFileSelected(event: any) {
    const file: File = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e: any) => {
        // e.target.result contiene la cadena Base64
        this.imagenBase64 = e.target.result;
      };
      reader.readAsDataURL(file);
    }
  }

  rol: string = 'usuario';

// 2. Actualiza la llamada al m√©todo registro
  registrar() {
    this.authService.registro(
      this.nombre, 
      this.username, 
      this.password, 
      this.imagenBase64,
      this.rol // Ahora enviamos el rol elegido en el HTML
    );
    this.router.navigate(['/login']);

  }
}
